com.mirth.connect.plugins.javascriptstep.script:
/*
 * Convert DateTime Stamp to Readable format
 */

msg['MSH']['MSH.8']['MSH.8.1'] = DateUtil.convertDate('yyyyMMddHHmmss', 'MM-dd-yyyy HH:mm:ss', msg['MSH']['MSH.8']['MSH.8.1'].toString());

/*
 * Convert Gender from Short to Generic Description
 */

var sourceGender = msg['PID']['PID.8']['PID.8.1'].toString();
var destinationGender = "Unknown";

if (sourceGender == "M") {
	destinationGender = "Male";
} else if (sourceGender == "F") {
	destinationGender = "Female";
}

msg['PID']['PID.8']['PID.8.1'] = destinationGender;

/*
 * Convert Race from Loinc to Generic Description
 */

var sourceRace = msg['PID']['PID.10']['PID.10.1'].toString();
var destinationRace = "Unknown";

if (sourceRace == "w") {
	destinationRace = "White";
}

msg['PID']['PID.10']['PID.10.1'] = destinationRace;

/*
 * Strips non numeric characters from work phone (PID-14)
*/

var i = 0;

while (msg['PID']['PID.14'][i] != null) {

	msg['PID']['PID.14'][i]['PID.14.1'] = msg['PID']['PID.14'][i]['PID.14.1'].toString().replace(/\D/g,'');

	i++;

}

/*
 * Convert Next Of Kin Relationship code to Generic Description
 */

var sourceNKRel = "";
var destinationNK1Rel = "";

for (var j = 0;j<msg['NK1'].length();j++) {

	sourceNKRel = msg['NK1'][j]['NK1.3']['NK1.3.1'].toString();
	destinationNK1Rel = "Unknown";

	if (sourceNKRel == "SPO") {
		destinationNK1Rel = "Spouse";
	} else if (sourceNKRel == "PAR") {
		destinationNK1Rel = "Parent";
	}

	msg['NK1'][j]['NK1.3']['NK1.3.1'] = destinationNK1Rel;

}

/*
 * Date of Birth Transformer
 * Converts DOB to a yyyy-MM-dd format
 */

var dob = msg['PID']['PID.7']['PID.7.1'].toString();
var formattedDOB = DateUtil.convertDate('yyyyMMdd', 'yyyy-MM-dd',dob);

tmp['DateOfBirth'] = formattedDOB;

/*
 * Gender Transformer
 * Translates Gender Code to Description
 */

var sourceGender = msg['PID']['PID.8']['PID.8.1'].toString();
var transformedGender = "Unknown";

if (sourceGender == "M" || sourceGender == "male") {
	transformedGender = "Male";
} else if (sourceGender == "F" || sourceGender == "female") {
	transformedGender = "Female";
}

tmp['Gender'] = transformedGender;

/*
 * Contact Information Transformer
 * Pulls the first phone number for each type as well as strips on Non Numeric characters
 */

var homePhone = "";
var workPhone = "";

var i = 0;

while (msg['PID']['PID.13'][i] != null) {

	if (homePhone == "") {
		homePhone = msg['PID']['PID.13'][i]['PID.13.1'].toString().replace(/\D/g,'');
	}

	i++;

	if (i == 100) {
		break;
	}

}

var j = 0;

while (msg['PID']['PID.14'][j] != null) {

	if (workPhone == "") {

		workPhone = msg['PID']['PID.14'][j]['PID.14.1'].toString().replace(/\D/g,'');

	}

	j++;

	if (j == 100) {
		break;
	}

}

tmp['HomePhone'] = homePhone;
tmp['WorkPhone'] = workPhone;

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
